    private void jbOKMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jbOKMouseClicked
        // TODO add your handling code here:
        String  tempCPF;
        int     tempNroConta;
        String  tempPrimeiroNome;
        String  tempSegundoNome;
        double  tempSaldo;
        
        tempCPF = jtfCPF.getText(); 
        tempNroConta = Integer.parseInt(jtfNroConta.getText()); 
        tempPrimeiroNome = jtfPrimeiroNome.getText();
        tempSegundoNome = jtfSegundoNome.getText();
        tempSaldo = Double.parseDouble(jtfSaldo.getText());
        
        ContaCliente  c = new ContaCliente();
        
        ContaCliente c3 = new ContaCliente(jtfCPF.getText(),
                Integer.parseInt(jtfNroConta.getText()), 
                jtfPrimeiroNome.getText(),
                jtfSegundoNome.getText(), 
                Double.parseDouble(jtfSaldo.getText()));
        
        c.setCPF(tempCPF);
        c.setNumero_conta(tempNroConta);
        c.setPrimeiro_nome(tempPrimeiroNome);
        c.setSegundo_nome(tempSegundoNome);
        c.setSaldo(tempSaldo);
        
        c.imprime();
        
    }//GEN-LAST:event_jbOKMouseClicked

