    private void jbAbrirMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jbAbrirMouseClicked
        arquivoConta.abreArquivoGrava();
    }//GEN-LAST:event_jbAbrirMouseClicked

    private void jbGravarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jbGravarMouseClicked
        ContaCliente conta = new ContaCliente(jtfCPF.getText(),
                Integer.parseInt(jtfNroConta.getText()), 
                jtfPrimeiroNome.getText(),
                jtfSegundoNome.getText(), 
                Double.parseDouble(jtfSaldo.getText()));
        
        arquivoConta.adicionaRegistro(conta.getCPF(), conta.getNumero_conta(),
                conta.getPrimeiro_nome(), conta.getSegundo_nome(),
                conta.getSaldo());
    }//GEN-LAST:event_jbGravarMouseClicked

    private void jbFecharMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jbFecharMouseClicked
        arquivoConta.fechaArquivoGrava();
    }//GEN-LAST:event_jbFecharMouseClicked

